<script>
	// kutu açma fonksiyonu
	function kutuAc() {
		document.getElementById("arkaPlan").style.display = "block";
	}
	function kutuAc2() {
		document.getElementById("arkaPlan2").style.display = "block";
	}
	function kutuAc3() {
		document.getElementById("arkaPlan3").style.display = "block";
	}

	// kutu kapama fonksiyonu
	function kutuKapat() {
		document.getElementById("arkaPlan").style.display = "none";
	}
	function kutuKapat2() {
		document.getElementById("arkaPlan2").style.display = "none";
	}
	function kutuKapat3() {
		document.getElementById("arkaPlan3").style.display = "none";
	}
	function kutuKapat4() {
		document.getElementById("menu").style.display = "none";
	}

	// ESC tuşuna basınca kutuyu kapat
	document.onkeydown = function(evt) {
		evt = evt || window.event;
		if (evt.keyCode == 27) {
			kutuKapat();
			kutuKapat2();
			kutuKapat3();
		}
	};

	// kutu dışındaki herhangi bir yere tıklanınca kutuyu kapat
	window.onclick = function(event) {
		var arkaPlan = document.getElementById("arkaPlan");
		var arkaPlan2 = document.getElementById("arkaPlan2");
		var arkaPlan3 = document.getElementById("arkaPlan3");
		if (event.target == arkaPlan) {
			kutuKapat();
		}else if (event.target == arkaPlan2) {
			kutuKapat2();
		}else if (event.target == arkaPlan3) {
			kutuKapat3();
		}
	};
</script>

